"use client"

import React from 'react'
import { Link } from 'react-router-dom'
import { useAuth } from '../contexts/AuthContext'

export default function Home() {
  const { user } = useAuth()

  return (
    <div className="text-center">
      <h1 className="text-4xl font-bold mb-4">Welcome to Movie Watchlist</h1>
      {user ? (
        <p className="mb-4">
          Hello, {user}! <Link to="/search" className="text-blue-600 hover:underline">Search for movies</Link> or <Link to="/watchlist" className="text-blue-600 hover:underline">view your watchlist</Link>.
        </p>
      ) : (
        <p className="mb-4">
          <Link to="/login" className="text-blue-600 hover:underline">Log in</Link> to start creating your watchlist!
        </p>
      )}
    </div>
  )
}
